<?php    /**     * @var @var core\View $this     * @var int $count     * @var int $page_limit     */    $countPages = ceil($count / $page_limit);    if ($countPages > 1) {        $currentPage = core\Request::getParam('page');        if ($currentPage == '') {            $currentPage = 1;        }        $pagination = '<ul class="pagination">';        for ($i = 1; $i <= $countPages; $i++) {            $isActive = $i == $currentPage;            $pagination .= $isActive ? '<li class="active">' : '<li>';            $pagination_url = $this->app->addUrlParam($this->app->pageUrl, 'page', $i);            $pagination .= $this->app->createLink($pagination_url, $i, $isActive);            $pagination .= '</li>';        }        $pagination .= '</ul>';        echo $pagination;    };