<?php    /**     * @var core\Controller $this     * @var int $count     */    $countPages = ceil($count / $this->pageLimit);    if ($countPages > 1) {        $currentPage = $this->app->getParam('page');        if ($currentPage == '') {            $currentPage = 1;        }        $pagination = '<ul class="pagination">';        for ($i = 1; $i <= $countPages; $i++) {            $isActive = $i == $currentPage;            $pagination .= $isActive ? '<li class="active">' : '<li>';            $pagination_url = $this->app->addUrlParam($this->app->pageUrl, 'page', $i);            $pagination .= $this->app->createLink($pagination_url, $i, $isActive);            $pagination .= '</li>';        }        $pagination .= '</ul>';        echo $pagination;    };