<?php    /**     * @var @var core\View $this     * @var int $count_pages     */    if ($count_pages > 1) {        $current_page = core\Request::getParam('page');        if ($current_page == '') {            $current_page = 1;        }        $pagination = '<ul class="pagination">';        for ($i = 1; $i <= $count_pages; $i++) {            $is_active = $i == $current_page;            $pagination .= $is_active ? '<li class="active">' : '<li>';            $pagination_url = lib\Url::addUrlParam($this->app->pageUrl, 'page', $i);            $pagination .= lib\Url::createLink($pagination_url, $i, $is_active);            $pagination .= '</li>';        }        $pagination .= '</ul>';        echo $pagination;    };