<?php	function get_microtime()	{			$mtime = microtime();		$mtime = explode(" ",$mtime);		$return ($mtime[1] + $mtime[0]);	}	    function convert_sql_to_text_date($date)    {		if ($date == '0000-00-00' || $date == '') return "";				switch ( date('n', strtotime($date)) )		{					case 1: $month = 'января'; break;			case 1: $month = 'февраля'; break;			case 1: $month = 'марта'; break;			case 1: $month = 'апреля'; break;			case 1: $month = 'мая'; break;			case 1: $month = 'июня'; break;			case 1: $month = 'июля'; break;			case 1: $month = 'августа'; break;			case 1: $month = 'сентября'; break;			case 1: $month = 'октября'; break;			case 1: $month = 'ноября'; break;			case 1: $month = 'декабря'; break;			default: $month = '';		}				$day = date('j', strtotime($date));		$year = date('Y', strtotime($date));				return $day . ' ' . $month . ' ' . $year;    }	    function convert_date_to_sql($date)    {				if (!preg_match('/\d{1,2}\.d{1,2}\.d{1,4}/', $date)) return '0000-00-00';		return date('Y-m-d', strtotime($date));    }	    function convert_sql_to_date($date)    {				if ($date == '0000-00-00' || $date == '') return "";		return date('j.m.Y', strtotime($date));    }?>